package com.oa.dao;import java.util.List;import java.util.Map;import com.oa.util.Paging;public interface EntityDao {		/** 	 * Criteria的查询	 * 	 * @param cs	 *            查询类	 * @param map	 *            查询条件 (key：字段名,value：字段值)	 * @param orderMap	 *            排序条件 (key：字段名,value：字段值)	 * @return	 */	public List<?> createCriteria(final Class<?> cs, final Map<String, Object> map,			Map<String, Object> orderMap);	/**	 * 带有分页的查询	 * 	 * @param cs	 *            查询类	 * @param map	 *            查询条件 key：字段名 value：字段值	 * @param curPage	 *            当前页码	 * @param pageSize	 *            每页显示条数	 * @return	 */	public Paging createQueryPaging(final Class<?> cs,			final Map<String, Object> map, final Map<String, Object> orderMap,			final Integer curPage, final Integer pageSize);		public Object saveOrUpdate(final Object model);	public void delete(final Object model);	public Object getObjectById(final Class<?> cs, final Integer id);	}